function toggle_tab(a) {
  a.parentElement.querySelector(".active").classList.remove("active");
  a.classList.add("active");
  document.querySelector(a.getAttribute("data-target")).parentElement.querySelector(".active").classList.remove("active");
  document.querySelector(a.getAttribute("data-target")).classList.add("active");
}